import Image from './image'
const Entry = sequelize.define('entry', {
  academicProgram: {
    allowNull: true,
    type: DataTypes.TEXT
  },
  awardWon: {
    type: DataTypes.TEXT
  },
  comment: {
    type: DataTypes.TEXT
  },
  distributionAllowed: {
    type: DataTypes.BOOLEAN,
  },
  entryType: {
    allowNull: false,
    type: DataTypes.INTEGER
  },
  entryId: {
    allowNull: false,
    type: DataTypes.INTEGER
  },
  excludeFromJudging: {
    allowNull: false,
    type: DataTypes.BOOLEAN,
    defaultValue: false
  },
  forSale: {
    allowNull: false,
    defaultValue: false,
    type: DataTypes.BOOLEAN
  },
  groupId: {
    allowNull: true,
    type: DataTypes.INTEGER,
    references: {
      model: 'groups',
      key: 'id'
    },
    onDelete: 'cascade',
    onUpdate: 'cascade'
  },
  invited: {
    allowNull: true,
    type: DataTypes.BOOLEAN
  },
  moreCopies: {
    allowNull: false,
    defaultValue: false,
    type: DataTypes.BOOLEAN
  },
  // The id of the portfolio the entry is associated with (only if showId is null)
  portfolioId: {
    allowNull: true,
    type: DataTypes.INTEGER,
    references: {
      model: 'portfolios',
      key: 'id'
    },
    onDelete: 'cascade',
    onUpdate: 'cascade'
  },
  showId: {
    allowNull: true,
    type: DataTypes.INTEGER,
    references: {
      model: 'shows',
      key: 'id'
    },
    onDelete: 'cascade',
    onUpdate: 'cascade'
  },
