query Portfolios($id: ID!) {
  periodPortfolios(portfolioPeriodId: $id) {
    id
    title
    createdAt
    entries{
        id
        title
        entryType
        forSale
        comment
        moreCopies
        ... on Video {
        provider
        videoId
        }
        ... on Photo {
        path
        }
        ... on OtherMedia {
        path
        }
    }
    score
  }
}
