    this.setState({
      alertVisible: false,
      alertType: ''
    })
  }
  handleRating = rating => {
    const { makePortfolioRating, handleError } = this.props

    makePortfolioRating(rating)
      .then(() => {
        this.setState({
          alertVisible: true
        })
        setTimeout(() => {
          this.onDismiss()
        }, 2000)
      })
      .catch(err => handleError(err.message))
  }
